import React from "react"
import ReactDOM from "react"
import './App.css';
import { useState } from "react"
import Navbar from "./Components/Navbar"

export default function App() {

  const [grid, setGrid] = useState(
    {
      rows:1,
      cols:1
    }
  );

  function addRow(){
    //console.log('add row');
    setGrid(prevGrid => ({
      ...prevGrid,
      rows: prevGrid.rows+1
    }))
  }

  function addColumn(){
    //console.log('add col');
    setGrid(prevGrid => ({
      ...prevGrid,
      cols: prevGrid.cols+1
    }))
  }
  for(let i=0;i<grid.cols;i++){
    const newCol = document.createElement("td")
    document.getElementById("table_div").appendChild(newCol)
    for(let j=0; j<grid.rows;j++){
      const newRow = document.createElement("tr")
      document.getElementById("table_div").appendChild(newRow)
    }
  }

    return (
      <div className="App">
        <Navbar />
        <div className="btn--container">
          <button className="nav--btn" onClick={addRow}>Add Row</button>
          <button className="nav--btn" onClick={addColumn}> Add Column</button>
          <input className="color--picker" type="color" value="#FF0000"/>
          <p>Rows: {grid.rows}</p>
          <p>Cols: {grid.cols}</p>
          <div id="table_div">
          </div>

          <table>
            {/* <AddRow /> */}
            {/* Table.jsx could be the parent of TableRow.jsx (presentational component)
              TableRow.jsx could be the parent of TableCell.jsx (presentational component) */}


          </table>
        </div>
      </div>
    );
}
